using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using BepInEx;
using UnityEngine;
using ESFisobs;
using Menu;
using RWCustom;

[BepInPlugin("LeeMoriya.ElectricSpear", "ElectricSpear", "0.1")]
public class ElectricSpearPlugin : BaseUnityPlugin
{
    public static BaseUnityPlugin instance;
    public ElectricSpearPlugin()
    {
        instance = this;
    }

    public void OnEnable()
    {
        //Fisob
        static IEnumerable<Fisob> GetFisobs()
        {
            yield return ElectricSpearFisob.Instance;
        }
        FisobRegistry reg = new(GetFisobs());
        reg.ApplyHooks();

        //Sprites
        eSpear = new Texture2D(32, 16, TextureFormat.ARGB32, false);
        eSpear.anisoLevel = 0;
        eSpear.filterMode = FilterMode.Point;
        byte[] bytes = Convert.FromBase64String(eSpearData);
        eSpear.LoadImage(bytes);

        //Hooks
        On.Player.Update += Player_Update;
        On.ItemSymbol.SpriteNameForItem += ItemSymbol_SpriteNameForItem;
        On.RainWorld.LoadResources += RainWorld_LoadResources;
        On.ScavengerTreasury.ctor += ScavengerTreasury_ctor;
        On.MultiplayerUnlocks.SandboxItemUnlocked += MultiplayerUnlocks_SandboxItemUnlocked;
        On.Menu.SandboxEditorSelector.ctor += SandboxEditorSelector_ctor;
        On.SandboxGameSession.SpawnEntity += SandboxGameSession_SpawnEntity;
        MultiplayerUnlocks.ItemsUnlocks += 1;
    }

    private void SandboxGameSession_SpawnEntity(On.SandboxGameSession.orig_SpawnEntity orig, SandboxGameSession self, ArenaBehaviors.SandboxEditor.PlacedIconData placedIconData)
    {
        if(placedIconData.data.itemType == ElectricSpearFisob.Instance.Type)
        {
            WorldCoordinate pos = new WorldCoordinate(0, -1, -1, -1);
            pos.x = Mathf.RoundToInt(placedIconData.pos.x / 20f);
            pos.y = Mathf.RoundToInt(placedIconData.pos.y / 20f);
            self.game.world.GetAbstractRoom(0).AddEntity(new AbstractElectricSpear(self.game.world, null, pos, placedIconData.ID, 3));
        }
        orig.Invoke(self, placedIconData);
    }

    private void SandboxEditorSelector_ctor(On.Menu.SandboxEditorSelector.orig_ctor orig, SandboxEditorSelector self, Menu.Menu menu, MenuObject owner, SandboxOverlayOwner overlayOwner)
    {
        orig.Invoke(self, menu, owner, overlayOwner);
        int num = MultiplayerUnlocks.ItemsUnlocks + 2;
        self.AddButton(new SandboxEditorSelector.CreatureOrItemButton(menu, self, MultiplayerUnlocks.SymbolDataForSandboxUnlock(EnumExt_ElectricSpearArena.electric_spear)), ref num);
        for (int w = 0; w < SandboxEditorSelector.Width; w++)
        {
            for (int h = 0; h < SandboxEditorSelector.Height; h++)
            {
                if (self.buttons[w, h] != null)
                {
                    self.buttons[w, h].Initiate(new IntVector2(w, h));
                }
            }
        }
    }

    private bool MultiplayerUnlocks_SandboxItemUnlocked(On.MultiplayerUnlocks.orig_SandboxItemUnlocked orig, MultiplayerUnlocks self, MultiplayerUnlocks.SandboxUnlockID unlockID)
    {
        if(unlockID == EnumExt_ElectricSpearArena.electric_spear)
        {
            return true;
        }
        return orig.Invoke(self, unlockID);
    }

    private void ScavengerTreasury_ctor(On.ScavengerTreasury.orig_ctor orig, ScavengerTreasury self, Room room, PlacedObject placedObj)
    {
        orig.Invoke(self, room, placedObj);
        if (room.abstractRoom.firstTimeRealized)
        {
            WorldCoordinate coord = room.GetWorldCoordinate(self.tiles[UnityEngine.Random.Range(0, self.tiles.Count - 1)]);
            room.abstractRoom.entities.Add(new AbstractElectricSpear(room.world, null, coord, room.game.GetNewID(), 3));
        }
    }

    private void RainWorld_LoadResources(On.RainWorld.orig_LoadResources orig, RainWorld self)
    {
        orig.Invoke(self);
        Futile.atlasManager.LoadAtlasFromTexture("eSpear", eSpear);
    }

    private string ItemSymbol_SpriteNameForItem(On.ItemSymbol.orig_SpriteNameForItem orig, AbstractPhysicalObject.AbstractObjectType itemType, int intData)
    {
        if(itemType == ElectricSpearFisob.Instance.Type)
        {
            return "eSpear";
        }
        return orig.Invoke(itemType, intData);
    }

    private void Player_Update(On.Player.orig_Update orig, Player self, bool eu)
    {
        orig.Invoke(self, eu);

        if(self != null && Input.GetKeyDown(KeyCode.Alpha9))
        {
            Debug.Log("Gonna spawn an Electric Spear");
            AbstractElectricSpear apo = new AbstractElectricSpear(self.room.world, null, self.abstractCreature.pos, self.room.game.GetNewID(), 3);
            self.room.abstractRoom.AddEntity(apo as AbstractElectricSpear);
            (apo as AbstractElectricSpear).RealizeInRoom();
            self.SlugcatGrab((apo as AbstractElectricSpear).realizedObject, 0);
            Debug.Log("Spawned Electric Spear!");
        }
    }

    public class EnumExt_ElectricSpearArena
    {
        public static MultiplayerUnlocks.SandboxUnlockID electric_spear;
    }

    public static Texture2D eSpear;
    public static string eSpearData = "";
}